import "./index.css";
import { React, useEffect, useState } from "react";
import Row from "./components/row";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


export default function App(props) {

  const [details, setDetails] = useState([]);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [id, setID] = useState("");
  const [button,setButton]=useState("Insert");
  const [tost,setTost]=useState("Fetched Successfully");
  const [offset,setOffset]=useState(0);
  const limit=10;
  const url ="http://localhost:9090/students";


  function fetchUsers(){
    fetch(url+"?offset="+offset+"&limit="+limit)
      .then((res) => res.json())
      .then((result) => {
        toast.success(tost)
        setDetails(result);

      }).catch(error=>{
        toast.error("Unable to fetch");
      });
  }
  useEffect(() => {
    window.removeEventListener('scroll', setOffset(limi));
    window.addEventListener('scroll', onScroll, { passive: true });
    fetchUsers();
  },[]);
  function deleteId(id) {
      let x=window.confirm("Are you sure you want to delete this record id "+ id)
      if(x){
        fetch(url + "/" + id, { method: "DELETE" })
      .then()
      .then(() => {
        setDetails(
          details.filter((prev) => {
            return id !== prev.id;
          })
        )
        toast.success("Deleted Successfully")
        ;
      });
      }
      handleNewRecord();
  }
  function editRow(id) {
    setButton("Update");
    let y = details.find((element) => {
      return element.id === id;
    });
    setName(y.name);
    setAge(y.age);
    setID(id)
  }

  function handleUpdate(event) {
    let button_name=(event.target.innerHTML);
    if(button_name==="Update"){
    let item = { name, age };

    fetch(url + "/" + id, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(item)
    })
      .then((res) => { setTost("Updated Successfully"); return res.json()})
      .then((result) => {

        fetchUsers();

      })
      .catch(error =>{
        toast.error("Failed to Insert");
      })
      ;
    setName("");
    setAge("");
    setID("");
    setButton("Insert");
    }
    else{
      if(name==="" || age===""){
        toast.warn("Please give input");
      }
      else{
        let item={ name, age };

      fetch(url , {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(item)
      })
        .then((res) => res.json())
        .then((result) => {
          setDetails(prev=>{
            return [...prev,result]
          })
          toast.success("Inserted Sucessfully");
        })
        .catch(error =>{
          toast.error("Failed to Insert");
        })
        ;
        setID("");
        setName("");
        setAge("");
      }

    }
  }
  function handleNewRecord(){
    setName("");
    setAge("");  
    setButton("Insert");
  }
  return (
    <div className="App">
      <h1>Basic React App</h1>
      <ToastContainer 
        position="top-right"
        autoClose={2000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover={false}
        theme="light"
      />
      <center> 
        <table className="main">
          <thead>
          <tr>
            <th>Student ID</th>
            <th>Student Name</th>
            <th>Student Age</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
          </thead>
          {(details.length===0)?<tr><td colSpan={5}><center>No Records</center></td></tr>:details.map((detail, index) => {
            return (
              <Row
                key={index}
                id={detail.id}
                name={detail.name}
                age={detail.age}
                onDelete={deleteId}
                onEdit={editRow}
              />
            );
          })}
        </table>
      </center>
      <div>
        <h2>{button}</h2>
        <center>
          <table className="child">

          {
            (button!=='Insert')?<tr>
            <td>ID</td>
            <td>
              <input
                type="text"
                value={id}
                readOnly
              />
            </td>
          </tr>:null
          }
            <tr>
              <td>Name</td>
              <td>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td>Age</td>
              <td>
                <input
                  type="text"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td colSpan="2">
                <center>
                  <button 
                    style={{ backgroundColor: "#4CAF50",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                  onClick={handleUpdate} >{button}</button>
                  <button
                    style={{ backgroundColor: "#afb3b1",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                    onClick={handleNewRecord}
                  >
                    Reset
                  </button>
                </center>
              </td>
            </tr>
          </table>
        </center>
      </div>
    </div>
  );
}
