import { useLocation } from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';

export default function Update(){

    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [id, setID] = useState("");
    const [button,setButton]=useState("Insert");
    const [tost,setTost]=useState("Fetched Successfully");
    const url ="http://localhost:9090/students";

    function handleUpdate(event) {
        let button_name=(event.target.innerHTML);
        if(0){
        let item = { name, age };

        fetch(url + "/" + id, {
          method: "PUT",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json"
          },
          body: JSON.stringify(item)
        })
          .then((res) => { setTost("Updated Successfully"); return res.json()})
          .then((result) => {

            fetchUsers();

          })
          .catch(error =>{
            toast.error("Failed to Insert");
          })
          ;
        setName("");
        setAge("");
        setID("");
        setButton("Insert");
        }
        else{
          if(name==="" || age===""){
            toast.warn("Please give input");
          }
          else{
            let item={ name, age };

          fetch(url , {
            method: "POST",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json"
            },
            body: JSON.stringify(item)
          })
            .then((res) => res.json())
            .then((result) => {
              setDetails(prev=>{
                return [...prev,result]
              })
              toast.success("Inserted Sucessfully");
            })
            .catch(error =>{
              toast.error("Failed to Insert");
            })
            ;
            setID("");
            setName("");
            setAge("");
          }

        }
      }
      function handleNewRecord(){
        setName("");
        setAge("");  
        setButton("Insert");
      }

    const location = useLocation() ;
    console.log(location.state);

    return(    
        <div>
        <h2>{button}</h2>
        <center>
          <table className="child">

          {
            (button!=='Insert')?<tr>
            <td>ID</td>
            <td>
              <input
                type="text"
                value={id}
                readOnly
              />
            </td>
          </tr>:null
          }
            <tr>
              <td>Name</td>
              <td>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td>Age</td>
              <td>
                <input
                  type="text"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td colSpan="2">
                <center>
                  <button 
                    style={{ backgroundColor: "#4CAF50",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                  onClick={handleUpdate} >{button}</button>
                  <button
                    style={{ backgroundColor: "#afb3b1",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                    onClick={handleNewRecord}
                  >
                    Reset
                  </button>
                </center>
              </td>
            </tr>
          </table>
        </center>
      </div>
    );
}
