import "./index.css";
import {useRef, React, useEffect, useState } from "react";
import Row from "./components/row";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


export default function App(props) {

  const [details, getDetails] = useState([]);
  const [name, getName] = useState("");
  const [age, getAge] = useState("");
  const [id, getID] = useState("");
  const [button,updateButton]=useState("Insert");
  const url ="http://localhost:9090/students";
  const ref=useRef(null);
  function getUsers(){
    fetch(url)
      .then((res) => res.json()
      )
      .then((result) => {
        getDetails(result);
        toast.success("Fetched Successfully");
      }).catch(error=>{
        toast.error("Unable to fetch");
      });
  }
  useEffect(() => {
    getUsers();
  },[]);
  function deleteId(id) {
    fetch(url + "/" + id, { method: "DELETE" })
      .then()
      .then(() => {
        getUsers();
      });
      handleNewRecord();
  }
  function editRow(id) {
    updateButton("Update");
    let y = details.find((element) => {
      return element.id === id;
    });
    getName(y.name);
    getAge(y.age);
    getID(id)
  }

  function handleUpdate(event) {
    let button_name=(event.target.innerHTML);
    if(button_name==="Update"){
      console.log("Update clicked")
    let item = { name, age };

    fetch(url + "/" + id, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(item)
    })
      .then((res) => res.json())
      .then((result) => {

      });
    getName("");
    getAge("");
    getID("");
    updateButton("Insert");
    }
    else{
      if(name==="" || age===""){
        window.alert("Please give input");
      }
      else{
        let item={ name, age };

      fetch(url , {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(item)
      })
        .then((res) => res.json())
        .then((result) => {
          getDetails(prev=>{
            return [...prev,result]
          })
          toast.success("Inserted Sucessfully");
        })
        .catch(error =>{
          toast.error("Failed Inserting");
        })
        ;
        getID("");
        getName("");
        getAge("");
      }

    }
  }
  function handleNewRecord(){
    getName("");
    getAge("");  
    updateButton("Insert");
  }
  return (
    <div className="App">
      <h1>Basic React App</h1>
      <ToastContainer />
      <center>
        <table className="main">
          <thead>
          <tr>
            <th>Student ID</th>
            <th>Student Name</th>
            <th>Student Age</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
          </thead>
          {(details.length===0)?<tr><td colSpan={5}><center>No Records</center></td></tr>:details.map((detail, index) => {
            return (
              <Row
                key={index}
                id={detail.id}
                name={detail.name}
                age={detail.age}
                onDelete={deleteId}
                onEdit={editRow}
              />
            );
          })}
        </table>
      </center>
      <div>
        <h2>Update Details</h2>
        <center>
          <table className="child">
            <tr>
              <td>New record?</td>
              <td><button onClick={handleNewRecord}>Yes</button></td>
            </tr>
          {
            (button!=='Insert')?<tr>
            <td>ID</td>
            <td>
              <input
                type="text"
                value={id}
                readOnly
              />
            </td>
          </tr>:null
          }
            <tr>
              <td>Name</td>
              <td>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => getName(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td>Age</td>
              <td>
                <input
                  type="text"
                  value={age}
                  onChange={(e) => getAge(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td colSpan="2">
                <center>
                  <button 
                    style={{ backgroundColor: "#4CAF50",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                  onClick={handleUpdate} >{button}</button>
                </center>
              </td>
            </tr>
          </table>
        </center>
      </div>
    </div>
  );
}
