            if(name==="" || age===""){
              toast.warn("Please give input");
            }
            else{
              let item={ name, age };

            fetch(url , {
              method: "POST",
              headers: {
                Accept: "application/json",
                "Content-Type": "application/json"
              },
              body: JSON.stringify(item)
            })
              .then((res) => res.json())
              .then((result) => {
                toast.success("Inserted Sucessfully");
                setTimeout(() => {
                  window.history.back();
                }, 1000);
              })
