        })
          .then((res) => res.json())
          .then((result) => {
            toast.success("Updated Successfully");
            setTimeout(() => {
                window.history.back();
              }, 1000);
          })
          .catch((error)=>
            {toast.error("Failed to Update");}
          )
          ;
          }
      }
      function handleNewRecord(){
        setName("");
        setAge("");  

      }
    return(    
        <div>

        <ToastContainer />
        <center>
        <h1>Insert Details</h1>
          <table className="child">

            <tr>
              <td>Name</td>
              <td>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td>Age</td>
              <td>
                <input
                  type="text"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                />
              </td>
            </tr>
            <tr>
              <td colSpan="2">
                <center>
                  <button 
                    style={{ backgroundColor: "#4CAF50",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                  onClick={handleInsert} >Insert</button>
                  <button
                    style={{ backgroundColor: "#afb3b1",
                    border: "none",
                    color: "white",
                    padding: "4px 12px",
                    textAlign: "center",
                    textDecoration: "none",
                    display: "inline-block",
                    fontSize: "16px",
                    cursor: "pointer",
                    borderRadius: "50px"}} 
                    onClick={handleNewRecord}
                  >
                    Reset
                  </button>
                </center>
              </td>
            </tr>
          </table>
        </center>
      </div>
    );
}
